    console.log(tempSearch);
    setOption(tempOption);
    if (tempSearch == "") {
      setSearch("''");
    } else {
      makeQuery(tempSearch);
    }

    setPending(true);
  };

  const makeQuery = (sent) => {
    var res = sent.split(" ");

    var newStr = "";
    for (var i = 0; i < res.length; i++) {
      if (i == res.length - 1) {
        newStr += res[i];
      } else {
        newStr += res[i] + "%20";
      }
    }

    setSearch(newStr);
    console.log(newStr);
  };

  const onHover = (id) => {
    setCurId(id);
    setHovered(true);
  };

  return (
    <div className="movies">
      <div id="filters">
        {/* {getData()}; */}
        <form onSubmit={handleSubmit}>
          <label>Sort by: </label>
          <select
            value={tempOption}
            onChange={(e) => setTempOption(e.target.value)}
          >
            <option value="Relevance">Relevance</option>
            <option value="Date">Date</option>
            <option value="Rating">Rating</option>
            <option value="Title">Title</option>
            <option value="FilmYear">FilmYear</option>
            <option value="Runtime">Runtime</option>
          </select>
          <label>Search: </label>
          <input
            type="text"
            value={tempSearch}
            onChange={(e) => setTempSearch(e.target.value)}
          />
          <button
            id="search"
            //  onClick={() => setAllValues()}
          >
            Search
          </button>
        </form>
      </div>

      <h2 id="mTitle">Results</h2>
      {pending && <Loading />}
      {console.log(movies)}
      {movies.map((movie) => (
        <div
          className="movie-preview"
          key={movie.netflixid}
          id={"movie" + movie.netflixid}
          onMouseEnter={() => onHover(movie.netflixid)}
          onMouseLeave={() => setHovered(false)}
        >
          <img src={checkImage(movie)} alt="Movie image" />
          <p>{he.decode(movie.title)}</p>
          
          {hovered && curId == movie.netflixid && <p>Rating: {Math.floor(movie.rating*100)/100}</p>}
          {hovered && curId == movie.netflixid && <p>Type: {movie.type}</p>}
          {hovered && curId == movie.netflixid && (
            <p>Runtime: {movie.runtime}</p>
          )}
        </div>
      ))}
      {!pending && (
        <Pagination pages={total} start={start} end={end} curPage={page} />
      )}
    </div>
  );
};

export default MovieList;
