      elements: {
        container: ".pindicator",
        bullet: ".bullet",
      },
      helpers: {
        past: "past",
        current: "current",
        next: "next",
        future: "future",
      },
    };

    var bulletEls;
    document.addEventListener("DOMContentLoaded", initBullets);

