import { useState, useEffect } from "react";

const useFetch = (url) => {
  const [data, setData] = useState([]);
  const [pending, setPending] = useState(true);
  const [error, setError] = useState(null);
  useEffect(() => {
    setTimeout(() => {
      fetch(url)
        .then((res) => {
          if (!res.ok) {
            throw Error("BOOHOO");
          }
          return res.json();
        })
        .then((data) => {
          setError(null);
          setPending(false);
          setData(data);
          console.log(data);
        })
        .catch((err) => {
          setError(err.message);
          setPending(false);
        });
    }, 1000);
  }, [url]);
  return { data, pending, error , set};
};

export default useFetch;
