const Pagination = (props) => {
  const pageNumbers = [];

  const paginate = () => {
    for (var i = 1; i <= Math.ceil(props.pages / 100); i++) {
      pageNumbers.push(i);
    }
  };
  return (
    <div>
      {paginate()}
      {console.log(props.pages)}
      <div id="pagination">
        {pageNumbers
          .filter((number) => number <= props.end && number >= props.start)
          .map((num) => (
            <div className="page-item" key={num}>
              {(num == props.curPage && (
                <a
                  href={`/movies/${num}`}
                  className="page-link"
                  style={{
                    color: "pink",
                  }}
                  // onClick={() => {
                  //   setEnd(num + 4);
                  //   setStart(num - 5);
                  // }}
                >
                  {num}
                </a>
              )) || (
                <a
                  href={`/movies/${num}`}
                  className="page-link"
                  //   onClick={() => {
                  //     setCurPage(num);
                  //     setEnd(num + 4);
                  //     setStart(num - 5);
                  //   }
                  // }
                >
                  {num}
                </a>
              )}
            </div>
          ))}
        {console.log(props.end)}
        {console.log(props.start)}
        <p>{Math.floor(props.pages / 100)} total pages</p>
      </div>
    </div>
  );
};

export default Pagination;
