  const { data, pending, setPending, error, movies, total } = useFetch(
    `https://unogs-unogs-v1.p.rapidapi.com/aaapi.cgi?q=${search}-!0%2C2021-!0%2C5-!0%2C10-!0-!Any-!Any-!Any-!gt0-!%7D&t=ns&cl=all&st=adv&ob=${option}&p=${curPage}`
  );

  useEffect(() => {
    setStart(Math.floor(curPage) - 5);
    setEnd(Math.floor(curPage) + 4);
    {
      window.scroll({
        top: 0,
        left: 0,
        behavior: "smooth",
      });
    }
    // setPending(true);
  }, [data]);
  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Option changed");
    console.log(tempSearch);
    setOption(tempOption);
    if (tempSearch == "") {
      setSearch("''");
    } else {
      makeQuery(tempSearch);
    }

    setPending(true);
  };

  const makeQuery = (sent) => {
    var res = sent.split(" ");

    var newStr = "";
    for (var i = 0; i < res.length; i++) {
      if (i == res.length - 1) {
        newStr += res[i];
      } else {
        newStr += res[i] + "%20";
      }
    }

    setSearch(newStr);
    setCurPage(1);
    console.log(newStr);
  };

  const onHover = (id) => {
    setHovered(true);
    setCurId(id);
  };

  const offHover = () => {
    setHovered(false);

  };

  return (
    <>
      <Navbar back={false}/>

      <div className="all">
        <div className="movies">
          <Container>
            <div
              id="filters"
              // className="d-flex justify-content-center"
            >

              <form onSubmit={handleSubmit}>
                <Row className="justify-content-center">
                  <Col
                    id="first"
                    className="col-lg-3 col-md-12 col-sm-12 col-xs-12 d-flex justify-content-center"
                  >
                    <label>Sort by:</label>
                    <select
                      value={tempOption}
                      onChange={(e) => setTempOption(e.target.value)}
                    >
                      <option value="Relevance">Relevance</option>
                      <option value="Date">Date</option>
                      <option value="Rating">Rating</option>
                      <option value="Title">Title</option>
                      <option value="FilmYear">FilmYear</option>
                      <option value="Runtime">Runtime</option>
                    </select>
                  </Col>
                  <Col
                    id="second"
                    className="col-lg-3 col-md-12 col-sm-12 col-xs-12 d-flex justify-content-center"
                  >
                    <label id="searchLabel">Search: </label>
                    <input
                      type="text"
                      value={tempSearch}
                      onChange={(e) => setTempSearch(e.target.value)}
                      id="searchInput"
                    />
                    {/* </Col>
              <Col className="col-md-12"> */}
                  </Col>
                  <Col
                    id="third"
                    className="col-lg-3 col-md-12 col-sm-12 col-xs-12 d-flex justify-content-center"
                  >
                    <Button
                      type="submit"
                      id="search"
                      className="btn-info btn-xs"
                      //  onClick={() => setAllValues()}
                    >
                      Search
                    </Button>
                  </Col>
                </Row>
              </form>
            </div>
          </Container>
          <Container id="allMovies">
            <h2 id="mTitle" className="text-center">
              Results
            </h2>
            <Row className="justify-content-center">
              <div id="resMovies">
                {pending && <Loading />}
                {console.log(movies)}
                {movies.map((movie) => (
                  <Col
                    className="movie-preview col-12"
                    key={movie.netflixid}
                    id={"movie" + movie.netflixid}
                    onMouseEnter={() => onHover(movie.netflixid)}
                    onMouseLeave={() => offHover()}
                  >
                    <Link to={`/movie/${movie.netflixid}`}>
                      <img src={checkImage(movie)} alt="Movie image" />
                      <p>{he.decode(movie.title)}</p>
                      {hovered && show && curId == movie.netflixid && (
                        <p>
                          {" "}
                          <span id="headings">Type:</span> {movie.type}
                        </p>
                      )}
                    </Link>
                  </Col>
                ))}
              </div>
            </Row>
          </Container>

          {!pending && (
            <Pagination
              pages={total}
              start={start}
              end={end}
              curPage={curPage}
              setCurPage={setCurPage}
              setPending={setPending}
            />
          )}
        </div>
      </div>
    </>
  );
};

export default MovieList;
