import { useState, useEffect } from "react";

const MovieList = () => {
  const [movies, setMovies] = useState([
    // {
    //   title: "ABCD",
    //   largeimage:
    //     "https://occ-0-2218-2219.1.nflxso.net/dnm/api/v6/evlCitJPPCVCry0BZlEFb5-QjKc/AAAABdP77c2lsCZEpif-gZf4g27Fv-aEA_f_WEv4KD59-k72CNh3m4kRSmlWg8IarbHm1Fg0CVw5AE9zxnp_PVHTJEyoqUhc.jpg?r=9e4",
    //   netflixid: 3,
    //   image:
    //     "https://occ-0-58-64.1.nflxso.net/dnm/api/v6/evlCitJPPCVCry0BZlEFb5-QjKc/AAAABeVXmHLEFrKXWQwlj5OUQQA2yWnH58rlerKRrkmWiCR8nlwzekJAJFp00kvaH2UL8eFU1YkBwnbR5y_a5kYuzbAUbrFO.jpg?r=7fb",
    // },
    // {
    //   title: "ABCDABCDABCDABCDABCDABCDABCDABCDABCDABCDABCDABCDABCDABCDABCD",
    //   largeimage: "http://cdn1.nflximg.net/images/8393/10868393.jpg",
    //   netflixid: 2,
    //   image:
    //     "https://occ-0-58-64.1.nflxso.net/dnm/api/v6/evlCitJPPCVCry0BZlEFb5-QjKc/AAAABeVXmHLEFrKXWQwlj5OUQQA2yWnH58rlerKRrkmWiCR8nlwzekJAJFp00kvaH2UL8eFU1YkBwnbR5y_a5kYuzbAUbrFO.jpg?r=7fb",
    // },
    // {
    //   title: "ABCD",
    //   largeimage:
    //     "https://occ-0-58-64.1.nflxso.net/dnm/api/v6/evlCitJPPCVCry0BZlEFb5-QjKc/AAAABeVXmHLEFrKXWQwlj5OUQQA2yWnH58rlerKRrkmWiCR8nlwzekJAJFp00kvaH2UL8eFU1YkBwnbR5y_a5kYuzbAUbrFO.jpg?r=7fb",
    //   netflixid: 1,
    //   image:
    //     "https://occ-0-58-64.1.nflxso.net/dnm/api/v6/evlCitJPPCVCry0BZlEFb5-QjKc/AAAABeVXmHLEFrKXWQwlj5OUQQA2yWnH58rlerKRrkmWiCR8nlwzekJAJFp00kvaH2UL8eFU1YkBwnbR5y_a5kYuzbAUbrFO.jpg?r=7fb",
    // },
    // {
    //   title: "ABCD",
    //   largeimage: "",
    //   image:
    //     "https://occ-0-58-64.1.nflxso.net/dnm/api/v6/evlCitJPPCVCry0BZlEFb5-QjKc/AAAABeVXmHLEFrKXWQwlj5OUQQA2yWnH58rlerKRrkmWiCR8nlwzekJAJFp00kvaH2UL8eFU1YkBwnbR5y_a5kYuzbAUbrFO.jpg?r=7fb",
    //   netflixid: 4,
    // },
  ]);

  const [total, setTotal] = useState(0);
  const pageNumbers = [];
  const paginate = () => {
    for (var i = 1; i <= total; i++) {
        if(i%100==0)
      pageNumbers.push(i);
    }
  };
  const checkImage = (movie) => {
    {
      var ans = movie.largeimage;
      if (ans == "") {
        ans = movie.image;
      }
      return ans;
    }
  };
  useEffect(() => {
    const abortCont = new AbortController();
    fetch(
      "https://unogs-unogs-v1.p.rapidapi.com/aaapi.cgi?q=Any-!0%2C2021-!0%2C5-!0%2C10-!0-!Any-!Any-!Any-!gt0-!%7Bdownloadable%7D&t=ns&cl=all&st=adv&ob=Relevance&p=6&sa=or",
      {
        method: "GET",
        headers: {
          "x-rapidapi-key":
            "3b8ba7d032msh9616bf36969a1a2p1a7015jsn5184b59caa2b",
          "x-rapidapi-host": "unogs-unogs-v1.p.rapidapi.com",
        },
      }
    )
      .then((response) => {
        return response.json();
      })
      .then((data) => {
        console.log(data);

        setMovies(data.ITEMS);
        setTotal(data.COUNT);
      })
      .catch((err) => {
        console.error(err);
      });
    return () => abortCont.abort();
  }, []);
  return (
    <div className="movies">
      <h2 id="mTitle">Netflix Movies</h2>
      {console.log(movies)}
      {movies.map((movie) => (
        <div className="movie-preview" key={movie.netflixid}>
          <img src={checkImage(movie)} alt="Movie image" />
          <p>{movie.title}</p>
        </div>
      ))}
      <p>{parseInt(total)}</p>
      {/* {dividePages()} */}
      {paginate()}
      {console.log(parseInt(total))}
      <ul>
        {pageNumbers.map((number) => (
          <li key={number} className="page-item">
            <a href="!#" className="page-link">
              {number}
            </a>
          </li>
        ))}
        <a>huyt</a>
      </ul>
    </div>
  );
};

export default MovieList;
