import React, { Component } from "react";
import { useState, useEffect, useRef } from "react";
import axios from "axios";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { Form, Button } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";

const CreateChilling = (props) => {
  const [title, setTitle] = useState(props.title);

  const [username, setUsername] = useState("");
  const [pass, setPass] = useState("");
  const [date, setDate] = useState(new Date());
  const [people, setPeople] = useState(0);
  const [duration, setDuration] = useState(0);
  const [language, setLanguage] = useState("");
  const [country, setCountry] = useState("");
  const [note, setNote] = useState("");

  const [usernameError, setUsernameError] = useState(false);
  const [peopleError, setPeopleError] = useState(false);
  const [durationError, setDurationError] = useState(false);
  const [noteError, setNoteError] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();

    console.log(typeof parseInt(people));
    console.log(duration);
    const chilling = {
      title: props.title,
      netflixid: parseInt(props.nid),
      username: username,
      pass: "legooo",
      date: date,
      people: parseInt(people),
      duration: parseInt(duration),
      language: language,
      country: country,
      note: note,
    };

    console.log(chilling);

    axios
      .post("http://localhost:5000/chillings/add", chilling)
      .then((res) => props.getAdded(true));

    // window.location = "/";
    setUsername("");
    setDate(new Date());
    setPeople(0);
    setDuration(0);
    setLanguage("");
    setCountry("");
    setNote("");
    {
      window.scroll({
        bottom: 0,
        left: 0,
        behavior: "smooth",
      });
    }
  };

  const onChangeDate = (date) => {
    setDate(date);
  };

  return (
    <div>
      <h3 className="text-center">Create New Chilling</h3>
      <Form id="chillingForm" onSubmit={handleSubmit}>
        <Form.Group className="mt-2">
          <Form.Label>Title: </Form.Label>
          <Form.Control
            type="text"
            required
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            width="10rem"
            readOnly="readonly"
          />
        </Form.Group>
        <Form.Group className="mt-2">
          <Form.Label>Username: </Form.Label>
          <Form.Control
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder="Username"
          />
          <Form.Text className="text-muted">
            The username you want to be displayed in the chilling info.
          </Form.Text>
        </Form.Group>
        <Form.Group className="mt-2">
          <Form.Label>Date: </Form.Label>
          <div>
            <DatePicker selected={date} onChange={onChangeDate} />
          </div>
        </Form.Group>

        <Form.Group className="mt-2">
          <Form.Label>Number of people: </Form.Label>
          <Form.Control
            type="text"
            value={people}
            onChange={(e) => setPeople(e.target.value)}
            placeholder="Enter Number of People"
          />
          <Form.Text className="text-muted">
            Number of expected participants.
          </Form.Text>
        </Form.Group>
        <Form.Group className="mt-2">
          <Form.Label>Duration (in minutes): </Form.Label>
          <Form.Control
            type="text"
            value={duration}
            onChange={(e) => setDuration(e.target.value)}
            placeholder="Enter Time Duration"
          />
        </Form.Group>
        <Form.Group className="mt-2">
          <Form.Label>Language: </Form.Label>
          {/* <Form.Control
            type="text"
            value={language}
            onChange={(e) => setLanguage(e.target.value)}
            placeholder="Enter Language"
          /> */}
          <select>
            
          </select>
        </Form.Group>
        <Form.Group className="mt-2">
          <Form.Label>Country: </Form.Label>
          <Form.Control
            type="text"
            value={country}
            onChange={(e) => setCountry(e.target.value)}
            placeholder="Enter Country"
          />
          <Form.Text className="text-muted">
            Different countries have different Netflix title availabilities.
          </Form.Text>
        </Form.Group>
        <Form.Group className="mt-2">
          <Form.Label>Note: </Form.Label>
          <Form.Control
            type="text"
            value={note}
            onChange={(e) => setNote(e.target.value)}
            placeholder="Enter Note"
          />
          <Form.Text className="text-muted">
            Enter contact detials or zoom link.
          </Form.Text>
        </Form.Group>
        <Form.Group className="mt-2">
          <Form.Control
            type="submit"
            value="Create Chilling Log"
            className="btn btn-info btn-lg mb-3"
          />
        </Form.Group>
      </Form>
    </div>
  );
};

export default CreateChilling;
