import { useState, useEffect } from "react";
import { useParams } from "react-router";
import useFetch from "./useFetch";
import Loading from "./Loading";
import he from "he";
import axios from "axios";
import CreateChilling from "./CreateChilling";
import { Button, Card } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";

const Movie = () => {
  const { netflixid } = useParams();
  const [wait, setWait] = useState(true);
  const [chill, setChill] = useState(true);
  const [added, setAdded] = useState(false);
  const [movie, setMovie] = useState([]);
  const [chillings, setChillings] = useState([]);

  const { data, pending, setPending, error, movies, total } = useFetch(
    `https://unogs-unogs-v1.p.rapidapi.com/aaapi.cgi?t=loadvideo&q=${netflixid}`
  );

  useEffect(() => {
    console.log(added);
    if (pending == false) {
      setWait(false);

      setMovie(data.RESULT.nfinfo);

      axios
        .get(`http://localhost:5000/chillings/movie/${netflixid}`)
        .then((response) => {
          setChillings(response.data);
          setChill(false);
          console.log(chillings);
          setAdded(false);
          //   console.log(response.data);
        })
        .catch((error) => {
          console.log(error);
        });
    }
  }, [data, added]);

  return (
    <div className="movie-details">
        {console.log(movie)}
      {wait ? (
        <Loading />
      ) : (
        <Card>
          <Card.Body>
            {(movie.image1 != "" && <Card.Img src={movie.image1} width='10px'/>) || (
              <Card.Img src={movie.image2} style="width: 10px;"/>
            )}
            <Card.Title>Title: {he.decode(movie.title)}</Card.Title>
            <Card.Text>Type: {movie.type}</Card.Text>
            {!movie.rating == "0" && (
              <Card.Text>Rating: {movie.avgrating}</Card.Text>
            )}
            <Card.Text>Released: {movie.released}</Card.Text>
            <Card.Text>Runtime: {movie.runtime}</Card.Text>
            <Card.Text>Synopsis: {he.decode(movie.synopsis)}</Card.Text>
          </Card.Body>
        </Card>
      )}

      {/* {console.log(netflixid)} */}
      {!wait && (
        <CreateChilling
          nid={netflixid}
          title={he.decode(movie.title)}
          getAdded={(added) => setAdded(added)}
        />
      )}

      <div id="allChilling">
        {!chill &&
          chillings.map((chilling) => (
            <div key={chilling._id}>
              <p>Username: {chilling.username}</p>
              <p>Date: {chilling.date}</p>
              <p>People: {chilling.people}</p>
              <p>Duration: {chilling.duration}</p>
              <p>Language: {chilling.language}</p>
              <p>Country: {chilling.country}</p>
              <p>Note: {chilling.note}</p>
            </div>
          ))}
      </div>
    </div>
  );
};

export default Movie;
