import { useState, useEffect } from "react";

const GetMovies = () => {
  const [movies, setMovies] = useState([]);

  const showMovies = (data) => {
    {
      data.map((movie) => {
        console.log(movie);
      });
    }
  };
  useEffect(() => {
    const abortCont = new AbortController();
    fetch(
      "https://unogs-unogs-v1.p.rapidapi.com/aaapi.cgi?q=Any-!0%2C2021-!0%2C5-!0%2C10-!0-!Any-!Any-!Any-!gt0-!%7Bdownloadable%7D&t=ns&cl=all&st=adv&ob=Relevance&p=1&sa=or",
      {
        method: "GET",
        headers: {
          "x-rapidapi-key":
            "3b8ba7d032msh9616bf36969a1a2p1a7015jsn5184b59caa2b",
          "x-rapidapi-host": "unogs-unogs-v1.p.rapidapi.com",
        },
      }
    )
      .then((response) => {
        return response.json();
      })
      .then((data) => {
        setMovies(data.ITEMS);
      })
      .catch((err) => {
        console.error(err);
      });
    return () => abortCont.abort();
  }, []);

  return (
    <center>
      <div className="movies">
        <h2 id="mTitle">Netflix Movies</h2>

        {movies.map((movie) => (
          <div className="movie-preview" key={movie.netflixid}>
            <img
              src={movie.image}
              // onerror={src="abc.jpg"}
            ></img>
            <p>{movie.title}</p>
            {/* <p>{movie.length}</p> */}
            {/* <p>{movie.genre}</p> */}
          </div>
        ))}
      </div>
    </center>
  );
};

export default GetMovies;
