import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import he from "he";

const MovieList = ({ page }) => {
  const [pending, setPending] = useState(false);
  const [error, setError] = useState(false);
  const [curPage, setCurPage] = useState(1);
  const [start, setStart] = useState(1);
  const [end, setEnd] = useState(10);
  const [movies, setMovies] = useState([
    // {
    //   title: "ABCD",
    //   largeimage:
    //     "https://occ-0-2218-2219.1.nflxso.net/dnm/api/v6/evlCitJPPCVCry0BZlEFb5-QjKc/AAAABdP77c2lsCZEpif-gZf4g27Fv-aEA_f_WEv4KD59-k72CNh3m4kRSmlWg8IarbHm1Fg0CVw5AE9zxnp_PVHTJEyoqUhc.jpg?r=9e4",
    //   netflixid: 3,
    //   image:
    //     "https://occ-0-58-64.1.nflxso.net/dnm/api/v6/evlCitJPPCVCry0BZlEFb5-QjKc/AAAABeVXmHLEFrKXWQwlj5OUQQA2yWnH58rlerKRrkmWiCR8nlwzekJAJFp00kvaH2UL8eFU1YkBwnbR5y_a5kYuzbAUbrFO.jpg?r=7fb",
    // },
    // {
    //   title: "ABCDABCDABCDABCDABCDABCDABCDABCDABCDABCDABCDABCDABCDABCDABCD",
    //   largeimage: "http://cdn1.nflximg.net/images/8393/10868393.jpg",
    //   netflixid: 2,
    //   image:
    //     "https://occ-0-58-64.1.nflxso.net/dnm/api/v6/evlCitJPPCVCry0BZlEFb5-QjKc/AAAABeVXmHLEFrKXWQwlj5OUQQA2yWnH58rlerKRrkmWiCR8nlwzekJAJFp00kvaH2UL8eFU1YkBwnbR5y_a5kYuzbAUbrFO.jpg?r=7fb",
    // },
    // {
    //   title: "5 &Agrave; il numero perfetto",
    //   largeimage:
    //     "https://occ-0-58-64.1.nflxso.net/dnm/api/v6/evlCitJPPCVCry0BZlEFb5-QjKc/AAAABeVXmHLEFrKXWQwlj5OUQQA2yWnH58rlerKRrkmWiCR8nlwzekJAJFp00kvaH2UL8eFU1YkBwnbR5y_a5kYuzbAUbrFO.jpg?r=7fb",
    //   netflixid: 1,
    //   image:
    //     "https://occ-0-58-64.1.nflxso.net/dnm/api/v6/evlCitJPPCVCry0BZlEFb5-QjKc/AAAABeVXmHLEFrKXWQwlj5OUQQA2yWnH58rlerKRrkmWiCR8nlwzekJAJFp00kvaH2UL8eFU1YkBwnbR5y_a5kYuzbAUbrFO.jpg?r=7fb",
    // },
    // {
    //   title: "An Actor&#39;s Revenge",
    //   largeimage: "",
    //   image:
    //     "https://occ-0-58-64.1.nflxso.net/dnm/api/v6/evlCitJPPCVCry0BZlEFb5-QjKc/AAAABeVXmHLEFrKXWQwlj5OUQQA2yWnH58rlerKRrkmWiCR8nlwzekJAJFp00kvaH2UL8eFU1YkBwnbR5y_a5kYuzbAUbrFO.jpg?r=7fb",
    //   netflixid: 4,
    // },
  ]);

  const [total, setTotal] = useState(1);
  const pageNumbers = [];
  const paginate = () => {
    for (var i = 1; i <= Math.ceil(total / 100); i++) {
      //   if (i % 100 == 0) {
      pageNumbers.push(i);
      //   }
    }
  };
  const checkImage = (movie) => {
    {
      var ans = movie.largeimage;
      if (ans == "") {
        ans = movie.image;
      }
      return ans;
    }
  };

  useEffect(() => {
    const abortCont = new AbortController();
    fetch(
      `https://unogs-unogs-v1.p.rapidapi.com/aaapi.cgi?%2C2021-!0%2C5-!0%7Bdownloadable%7D&t=ns&cl=all&st=adv&ob=Relevance&p=${curPage}&sa=or`,
      {
        method: "GET",
        headers: {
          "x-rapidapi-key":
            "3b8ba7d032msh9616bf36969a1a2p1a7015jsn5184b59caa2b",
          "x-rapidapi-host": "unogs-unogs-v1.p.rapidapi.com",
        },
      }
    )
      .then((response) => {
        return response.json();
      })
      .then((data) => {
        console.log(data);

        setMovies(data.ITEMS);
        setTotal(data.COUNT);
      })
      .catch((err) => {
        console.error(err);
      });

    return () => abortCont.abort();
  }, []);
  return (
    <div className="movies">
      <h2 id="mTitle">Netflix Movies</h2>
      {console.log(movies)}
      {movies.map((movie) => (
        <div className="movie-preview" key={movie.netflixid}>
          <img src={checkImage(movie)} alt="Movie image" />

          <p>{he.decode(movie.title)}</p>
        </div>
      ))}
      {paginate()}
      {console.log(parseInt(total))}
      {/* <ul> */}
      <div>
        {pageNumbers
          .filter((number) => number <= 10)
          .map((num) => (
            <div className="page-item">
              {(num == page && (
                <Link
                  to={`/movies/${num}`}
                  className="page-link"
                  style={{
                    color: "pink",
                  }}
                  onClick={() => {
                    setEnd(num + 4);
                    setStart(num - 5);
                  }}
                >
                  {num}
                </Link>
              )) || (
                <Link
                  to={`/movies/${num}`}
                  className="page-link"
                  onClick={() => {
                    setCurPage(num);
                    setEnd(num + 4);
                    setStart(num - 5);
                  }}
                >
                  {num}
                </Link>
              )}
            </div>
          ))}
      </div>
      {/* {pageNumbers.filter((number) => number <= 5).map((num) => ({ num }))} */}
      {/* {pageNumbers.map((number) => (
          <div key={number} className="page-item">
            {number>5 && number>=pageNumbers.length-3 && <span>.</span>}
            {number<5 && <a href={`/movies/${number}`} className="page-link">
              {number}
            </a>}
            
          </div>
        ))} */}
      {/* </ul> */}

      {/* {dividePages()} */}
    </div>
  );
};

export default MovieList;
