import { useState, useEffect } from "react";
import { useParams } from "react-router";
import useFetch from "./useFetch";
import Loading from "./Loading";

const Movie = () => {
  const { netflixid } = useParams();
  const [wait, setWait] = useState(true);

  const searchMovie = () => {};
  const { data, pending, setPending, error, movies, total } = useFetch(
    `https://unogs-unogs-v1.p.rapidapi.com/aaapi.cgi?t=loadvideo&q=${netflixid}`
  );

  useEffect(() => {
    if (pending == false) {
      setWait(false);
    }

    if (wait == false) {
      //   setMovie(data.RESULT.nfinfo);
      const [movie, setMovie] = useState([]);
    }
  }, [pending]);
  return (
    <div className="blog-details">
      {wait && <Loading />}
      {!wait && console.log(data.RESULT.nfinfo)}

      {/* {!pending && console.log(netflixid)}
      {!pending && setMovie(data.RESULT.nfinfo)}
      {console.log(movie)} */}

      {!wait && (
        <article>
          {(!movie.image1 == "" && <img src={movie.image2}></img>) || (
            <img src={movie.image}></img>
          )}
          <h2>Title: {movie.title}</h2>
          <p>Type: {movie.type}</p>
          {!movie.rating == 0 && <p>Rating: {movie.avgrating}</p>}
          <p>Released: {movie.released}</p>
          <p>Runtime: {movie.runtime}</p>
          <p>Synopsis: {movie.synopsis}</p>
        </article>
      )}
    </div>
  );
};

export default Movie;
