      )}

      {!pending && !error && (
        <BlogList
          blogs={blogs.filter((blog) => blog.author === "mario")}
          title="Mario's blogs"
          handleDel={handleDel}
        />
      )}
      <button onClick={() => setName("Shehzil")}>Change Name</button>
    </div>
  );
};

