import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import he from "he";
import Pagination from "./Pagination";
import Loading from "./Loading";
import useFetch from "./useFetch";

const MovieList = ({ page }) => {
  // const [pending, setPending] = useState(true);
  // const [error, setError] = useState(false);
  const [option, setOption] = useState("Relevance");
  const [tempOption, setTempOption] = useState("Relevance");
  const [tempSearch, setTempSearch] = useState("");
  const [search, setSearch] = useState("''");
  const [start, setStart] = useState(Math.floor(page) - 5);
  const [end, setEnd] = useState(Math.floor(page) + 4);
  // const [movies, setMovies] = useState([
  // {
  //   title: "ABCD",
  //   largeimage:
  //     "https://occ-0-2218-2219.1.nflxso.net/dnm/api/v6/evlCitJPPCVCry0BZlEFb5-QjKc/AAAABdP77c2lsCZEpif-gZf4g27Fv-aEA_f_WEv4KD59-k72CNh3m4kRSmlWg8IarbHm1Fg0CVw5AE9zxnp_PVHTJEyoqUhc.jpg?r=9e4",
  //   netflixid: 3,
  //   image:
  //     "https://occ-0-58-64.1.nflxso.net/dnm/api/v6/evlCitJPPCVCry0BZlEFb5-QjKc/AAAABeVXmHLEFrKXWQwlj5OUQQA2yWnH58rlerKRrkmWiCR8nlwzekJAJFp00kvaH2UL8eFU1YkBwnbR5y_a5kYuzbAUbrFO.jpg?r=7fb",
  // },
  // {
  //   title: "ABCDABCDABCDABCDABCDABCDABCDABCDABCDABCDABCDABCDABCDABCDABCD",
  //   largeimage: "http://cdn1.nflximg.net/images/8393/10868393.jpg",
  //   netflixid: 2,
  //   image:
  //     "https://occ-0-58-64.1.nflxso.net/dnm/api/v6/evlCitJPPCVCry0BZlEFb5-QjKc/AAAABeVXmHLEFrKXWQwlj5OUQQA2yWnH58rlerKRrkmWiCR8nlwzekJAJFp00kvaH2UL8eFU1YkBwnbR5y_a5kYuzbAUbrFO.jpg?r=7fb",
  // },
  // {
  //   title: "5 &Agrave; il numero perfetto",
  //   largeimage:
  //     "https://occ-0-58-64.1.nflxso.net/dnm/api/v6/evlCitJPPCVCry0BZlEFb5-QjKc/AAAABeVXmHLEFrKXWQwlj5OUQQA2yWnH58rlerKRrkmWiCR8nlwzekJAJFp00kvaH2UL8eFU1YkBwnbR5y_a5kYuzbAUbrFO.jpg?r=7fb",
  //   netflixid: 1,
  //   image:
  //     "https://occ-0-58-64.1.nflxso.net/dnm/api/v6/evlCitJPPCVCry0BZlEFb5-QjKc/AAAABeVXmHLEFrKXWQwlj5OUQQA2yWnH58rlerKRrkmWiCR8nlwzekJAJFp00kvaH2UL8eFU1YkBwnbR5y_a5kYuzbAUbrFO.jpg?r=7fb",
  // },
  // {
  //   title: "An Actor&#39;s Revenge",
  //   largeimage: "",
  //   image:
  //     "https://occ-0-58-64.1.nflxso.net/dnm/api/v6/evlCitJPPCVCry0BZlEFb5-QjKc/AAAABeVXmHLEFrKXWQwlj5OUQQA2yWnH58rlerKRrkmWiCR8nlwzekJAJFp00kvaH2UL8eFU1YkBwnbR5y_a5kYuzbAUbrFO.jpg?r=7fb",
  //   netflixid: 4,
  // },
  // ]);

  // const [total, setTotal] = useState(1);

  const checkImage = (movie) => {
    {
      var ans = movie.largeimage;
      if (ans == "") {
        ans = movie.image;
      }
      return ans;
    }
  };

  // const getData = () => {
  //   useEffect(() => {
  //     const abortCont = new AbortController();
  //     fetch(
  //       // `https://unogs-unogs-v1.p.rapidapi.com/aaapi.cgi?%2C2021-!0%2C5-!0%7D&t=ns&cl=all&st=adv&ob=Relevance&p=${page}&sa=or`,
  //       `https://unogs-unogs-v1.p.rapidapi.com/aaapi.cgi?q=beyond%20stranger%20things-!0%2C2021-!0%2C5-!0%2C10-!0-!Any-!Any-!Any-!gt0-!%7D&t=ns&cl=all&st=adv&ob=${option}&p=1`,
  //       {
  //         method: "GET",
  //         headers: {
  //           "x-rapidapi-key":
  //             "3b8ba7d032msh9616bf36969a1a2p1a7015jsn5184b59caa2b",
  //           "x-rapidapi-host": "unogs-unogs-v1.p.rapidapi.com",
  //         },
  //       }
  //     )
  //       .then((response) => {
  //         return response.json();
  //       })
  //       .then((data) => {
  //         console.log(data);
  //         console.log(option);
  //         setMovies(data.ITEMS);
  //         setTotal(data.COUNT);
  //         setPending(false);
  //       })
  //       .catch((err) => {
  //         console.error(err);
  //         setError(true);
  //       });

  //     return () => abortCont.abort();
  //   }, []);
  // };

  const { data, pending, setPending, error, movies, total } = useFetch(
    `https://unogs-unogs-v1.p.rapidapi.com/aaapi.cgi?q=${search}-!0%2C2021-!0%2C5-!0%2C10-!0-!Any-!Any-!Any-!gt0-!%7D&t=ns&cl=all&st=adv&ob=${option}&p=1`
  );

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Option changed");
    
    setOption(tempOption);
    if(tempSearch!=""){
      makeQuery(tempSearch);
    }
    
    setPending(true);
  };

  const makeQuery = (sent) => {
    var res = sent.split(" ");

    var newStr ='';
    for(var i =0; i<res.length; i++){
      if(i==res.length-1){
        newStr+=res[i];
      }else{
        newStr+=res[i]+'%20';
      }
    }

    setSearch(newStr);
    console.log(newStr);
  }

  return (
    <div className="movies">
      <div id="filters">
        {/* {getData()}; */}
        <form onSubmit={handleSubmit}>
          <label>Sort by: </label>
          <select
            value={tempOption}
            onChange={(e) => setTempOption(e.target.value)}
          >
            <option value="Relevance">Relevance</option>
            <option value="Date">Date</option>
            <option value="Rating">Rating</option>
            <option value="Title">Title</option>
            <option value="FilmYear">FilmYear</option>
            <option value="Runtime">Runtime</option>
          </select>
          <label>Search: </label>
          <input
          type="text"
          value={tempSearch}
          onChange={(e) => setTempSearch(e.target.value)}
        />
          <button
            id="search"
            //  onClick={() => setAllValues()}
          >
            Search
          </button>
        </form>
      </div>

      <h2 id="mTitle">Results</h2>
      {pending && <Loading />}
      {console.log(movies)}
      {movies.map((movie) => (
        <div className="movie-preview" key={movie.netflixid}>
          <img src={checkImage(movie)} alt="Movie image" />

          <p>{he.decode(movie.title)}</p>
        </div>
      ))}
      {!pending && (
        <Pagination pages={total} start={start} end={end} curPage={page} />
      )}
    </div>
  );
};

export default MovieList;
