import { useState, useEffect } from "react";

const GetMovies = () => {
  const [movies, setMovies] = useState([
    // {
    //   title: "ABCD",
    //   image:
    //     "https://occ-0-138-38.1.nflxso.net/dnm/api/v6/evlCitJPPCVCry0BZlEFb5-QjKc/AAAABdqjmNngPZwIDQE_NrYfjryOzTC25a-N5hpfAcvDzUQzG_g8Dn8LrYhn4VhU9QQlgNv_5Z1dJZF2UBqvkeb2hfOzAQ.jpg?r=7fb",
    //   netflixid: 3,
    // },
    // {
    //   title: "ABCD",
    //   image:
    //     "http://art-2.nflximg.net/63fac/79c52ccb0931cc50f51fd8922ecaef551d263fac.jpg",
    //   netflixid: 1,
    // },
  ]);
  const [newUrl, setNewUrl] = useState("");

  const showMovies = (data) => {
    {
      data.map((movie) => {
        console.log(movie);
      });
    }
  };
  useEffect(() => {
    const abortCont = new AbortController();
    fetch(
      "https://unogs-unogs-v1.p.rapidapi.com/aaapi.cgi?q=Any-!0%2C2021-!0%2C5-!0%2C10-!0-!Any-!Any-!Any-!gt0-!%7Bdownloadable%7D&t=ns&cl=all&st=adv&ob=Relevance&p=1&sa=or",
      {
        method: "GET",
        headers: {
          "x-rapidapi-key":
            "3b8ba7d032msh9616bf36969a1a2p1a7015jsn5184b59caa2b",
          "x-rapidapi-host": "unogs-unogs-v1.p.rapidapi.com",
        },
      }
    )
      .then((response) => {
        return response.json();
      })
      .then((data) => {
        setMovies(data.ITEMS);
      })
      .catch((err) => {
        console.error(err);
      });
    return () => abortCont.abort();
  }, [movies]);

  const imgError = (imageSrc) => {
    var http = new XMLHttpRequest();
    http.open("HEAD", imageSrc, false);
    http.setRequestHeader('Access-Control-Allow-Headers', '*');
    http.setRequestHeader('Access-Control-Allow-Origin', '*');
   
    http.send();

    if (http.status == 403) {
      imageSrc =
        "http://art-2.nflximg.net/63fac/79c52ccb0931cc50f51fd8922ecaef551d263fac.jpg";
    }
    console.log(http.status);

    return imageSrc;
  };

  return (
    <center>
      <div className="movies">
        <h2 id="mTitle">Netflix Movies</h2>
      {console.log(movies)}
        {movies.map((movie) => (
          // setNewUrl(movie.image),

          <div className="movie-preview" key={movie.netflixid}>
            {/* {setNewUrl(movie.image)} */}

            {/* {setNewUrl(movie.image)} */}
            <img src={(movie.largeimage)} alt="Movie image"/>
            <p>{movie.title}</p>
            {/* <p>{movie.length}</p> */}
            {/* <p>{movie.genre}</p> */}
          </div>
        ))}
      </div>
    </center>
  );
};

export default GetMovies;
