import { Link } from "react-router-dom";

const Pagination = (props) => {
  const pageNumbers = [];

  const paginate = () => {
    for (var i = 1; i <= Math.ceil(props.pages / 100); i++) {
      pageNumbers.push(i);
    }
  };
  return (
    <div>
      {paginate()}
      {console.log(props.pages)}
      <div id="pagination">
        {pageNumbers
          .filter((number) => number <= props.end && number >= props.start)
          .map((num) => (
            <div className="page-item" key={num}>
              {(num == props.curPage && (
                <Link
                  to={`/movies/${num}`}
                  className="page-link"
                  style={{
                    color: "pink",
                  }}
                  onClick= {()=>{
                    props.setCurPage(num);
                  }}
                  // onClick={() => {
                  //   setEnd(num + 4);
                  //   setStart(num - 5);
                  // }}
                >
                  {num}
                </Link>
              )) || (
                <Link
                  to={`/movies/${num}`}
                  // className="page-link"
                  //   onClick={() => {
                  //     setCurPage(num);
                  //     setEnd(num + 4);
                  //     setStart(num - 5);
                  //   }
                  // }
                  onClick= {()=>{
                    props.setCurPage(num);
                    props.setPending(true);
                  }}
                >
                  {num}
                </Link>
              )}
            </div>
          ))}
        {console.log(props.end)}
        {console.log(props.start)}
        <p>{Math.floor(props.pages / 100)} total pages</p>
      </div>
    </div>
  );
};

export default Pagination;
