import { useState, useEffect } from "react";
import { useParams } from "react-router";
import useFetch from "./useFetch";
import Loading from "./Loading";
import he from "he";
import axios from "axios";
import CreateChilling from "./CreateChilling";

const Movie = () => {
  const { netflixid } = useParams();
  const [wait, setWait] = useState(true);
  const [movie, setMovie] = useState([]);
  const searchMovie = () => {};
  const { data, pending, setPending, error, movies, total } = useFetch(
    `https://unogs-unogs-v1.p.rapidapi.com/aaapi.cgi?t=loadvideo&q=${netflixid}`
  );

  useEffect(() => {
    if (pending == false) {
      setWait(false);

      setMovie(data.RESULT.nfinfo);
      console.log(movie);

      axios
        .get(`http://localhost:5000/chillings/movie/${netflixid}`)
        .then((response) => {
          console.log(response.data);
        })
        .catch((error) => {
          console.log(error);
        });
    }
  }, [data]);

  return (
    <div className="movie-details">
      {wait ? (
        <Loading />
      ) : (
        <article>
          {(!movie.image1 == "" && <img src={movie.image2}></img>) || (
            <img src={movie.image}></img>
          )}
          <h2>Title: {he.decode(movie.title)}</h2>
          <p>Type: {movie.type}</p>
          {!movie.rating == "0" && <p>Rating: {movie.avgrating}</p>}
          <p>Released: {movie.released}</p>
          <p>Runtime: {movie.runtime}</p>
          <p>Synopsis: {he.decode(movie.synopsis)}</p>
        </article>
      )}

            {console.log(netflixid)}
      <CreateChilling nid={netflixid} title={he.decodemovie.title}/>
    </div>
  );
};

export default Movie;
