import { useParams } from "react-router";


const BlogDetails = () => {
  const { id } = useParams();
  const {
    data: blog,
    error,
    pending,
  } = useFetch("http://localhost:8000/blogs/" + id);
  return (
    <div className="blog-deatils">
      {pending && <div>Loading...</div>}
      {error && <div>{error}</div>}
      {blog && (
          <article>
              <h2>
                {blog.title}
              </h2>
              <p>
                Written by {blog.author}
              </p>
              <div>
                  {blog.body}
              </div>
          </article>
      )}
    </div>
  );
};

export default BlogDetails;
